/* ===全局字体=== */
@import url("https://fontsapi-storage.zeoseven.com/519/main/result.css");

body,
input,
select,
h3>span,
textarea,
ul>li,
.text_pole,
.mes_text pre code,
.menu_button>span,
#send_textarea:placeholder-shown  {
    font-family: "MaoKenWangXingYuan";
    font-weight: normal;
}

/* ===聊天界面布局代码，引用自类脑社区双色盒子美化作品，作者@kakaa=== */
:root {
  --mes-width: 100%;
  /* 此百分比调整聊天气泡与整体聊天界面的距离 */
  --mes-block-width: 98%;
  /* 此百分比调整聊天记录文字与气泡左右边框的距离 */
  --box-border-radius: 5px;
  /* 所有盒子的圆角大小 */
  --top-distance: 5px;
  /* 顶栏与上边沿的距离 */
  --bottom-distance: 5px;
  /* 底栏与下边沿的距离 */
  --chat-send-spacing: 5px;
  /* 聊天界面与发送框的距离 */
  --leftRight-chat-spacing: 10px;
  /* 聊天界面与左右边沿的距离，电脑端专供 */
  --scroll-width: 5px;
  /* 滚动条宽度 */
  --icon-size: var(--mainFontSize);
  /* 图标大小 */
  --border-radius-small: 5px;
  --border-radius-medium: 10px;
  --spacing-long: 10px;
  --spacing-short: 5px;
}

#chat {
  overflow-y: scroll;
  align-items: center;
  max-height: calc(100dvh - var(--topBarBlockSize) - 20px) !important;
  padding: calc(var(--scroll-width) + 4px) 0 var(--scroll-width)
    var(--scroll-width);
  border-radius: 0 0 var(--box-border-radius) var(--box-border-radius);
  background-color: var(--chat-background-color);
  border: none;
}

@supports (-webkit-touch-callout: none) {
  #chat {
    padding-right: var(--scroll-width);
  }
}

.mesAvatarWrapper {
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.mesAvatarWrapper .avatar {
  margin: calc(var(--mainFontSize)) 0 calc(var(--mainFontSize) * 0.4) 0;
}

.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: contents;
  color: var(--text-color-sec);
}

#chat .ch_name {
  position: relative;
  align-items: flex-end;
  padding: calc(var(--mainFontSize) * 0.8) 0;
}

#chat .ch_name::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 2px;
  background: linear-gradient(to right,#80A8F8,#D0A8B0);
} 

.mes {
  width: var(--mes-width);
  flex-direction: column;
  align-items: center;
  border: none !important;
  background-color: var(--ui-color-main) !important;
  margin: 0;
}

.mes_text {
  padding: calc(var(--mainFontSize) * 0.8) 0 0 0;
}

.mes_block {
  width: var(--mes-block-width);
  padding-left: 0;
}

.last_mes .mes_text {
  padding-right: 0;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0;
}

