}

#top-settings-holder {
   border: none!important;
   box-shadow: 2px 2px 2px rgba(0,0,0,0.5);
}

#send_form {
   border: none;
   box-shadow: -2px -2px 2px rgba(0,0,0,0.5);
}

.menu_button {
   border: none;
