}

input[type="range"] {
  border-radius: 6px!important;
  background: linear-gradient(to right,#80A8F8,#D0A8B0)!important;
  background-blend-mode: overlay;
  box-shadow: none!important;
  outline: none;
}

input[type="range"]::-webkit-slider-thumb {
    position: relative;
    height: 25px; 
    width: 25px; 
    box-shadow: none;
    background: transparent url(https://i.ibb.co/4wFGtrBt/03.png) no-repeat center/contain;
}

textarea {
   border: none;
   border-bottom: 2px dashed skyblue;
   background: white;
}

hr,
#completion_prompt_manager .completion_prompt_manager_list_separator hr {
   width: 100%;
   height: 20px;
   background: url(https://files.catbox.moe/x034t5.png) no-repeat center/contain;
   opacity: 1;
}

input[type='checkbox'] {
  width: 14px;
  height: 14px;
  cursor: pointer;
  outline: none;
  background: rgba(255,255,255,0.8);
  border: none;
  box-shadow: rgba(255,255,255,0.5);
}

input[type='checkbox']:checked {
  background: transparent;
  box-shadow: none;
}

input[type='checkbox']:checked::after {
  content: "😺";
  position: absolute;
  font-size: 12px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
}

