/* ===聊天界面设计=== */
.mes[is_user='false'] .mesAvatarWrapper {
   align-items: flex-start;
   width: 100%;
   border-radius: 6px 6px 0 0;
   background: url(https://files.catbox.moe/h91qqo.jpg) no-repeat bottom/cover;
}

.mes[is_user='false'] .mesAvatarWrapper .avatar {
   transform: rotate(-13deg) translateY(-18px);
}

.mes[is_user='false'] .mesAvatarWrapper .avatar::before {
   content: "";
   position: absolute;
   top: -39px;
   left: -25px;
   width: 100px;
   height: 100px;
   background: url(https://files.catbox.moe/l0lipf.png) no-repeat center/contain;
   transform: scale(0.84);
}

.mes_button.mes_edit::before,
.mes_button.extraMesButtonsHint::before {
   display: none;
}

.mes_button.mes_edit,
.mes_button.extraMesButtonsHint {
   width: 30px;
   height: 30px;
   opacity: 1;
   filter: none !important;
}

.mes_button.mes_edit {
   background: url(https://files.catbox.moe/ejizot.png) no-repeat center/contain;
}

.mes_button.extraMesButtonsHint {
   background: url(https://files.catbox.moe/iylonf.png) no-repeat center/contain;
}

.mes[is_user='true'] .mesAvatarWrapper {
   align-items: flex-end;
   width: 100%;
   border-radius: 6px 6px 0 0;
   background: url(https://files.catbox.moe/k7un8k.jpg) no-repeat bottom/cover;
}

.mes[is_user='true'] .mesAvatarWrapper .avatar {
   transform: rotate(13deg) translateY(-18px);
}

.mes[is_user='true'] .mesAvatarWrapper .avatar::before {
   content: "";
   position: absolute;
   top: -39px;
   right: -25px;
   width: 100px;
   height: 100px;
   background: url(https://files.catbox.moe/8lc98o.png) no-repeat center/contain;
   transform: scale(0.84);
}

.mes_reasoning_header {
   padding-left: 20px;
