}

/* ===角色卡面板=== */
.character_select:nth-child(even) {
   border: none;
   border-bottom: 2px dashed skyblue;
   background: RGBA(216,191,216,0.5) url(https://files.catbox.moe/i076j8.png) no-repeat 10rem/contain;
}

.character_select:nth-child(odd) {
   border: none;
   border-bottom: 2px dashed pink;
   background: RGBA(161,207,229,0.5) url(https://files.catbox.moe/i5o83g.png) no-repeat right/contain;
}

#description_textarea {  
   background: white url(https://files.catbox.moe/0e663g.png) no-repeat bottom/contain;
}

/* ===聊天文本样式=== */
q {
   display: inline-block;
   position: relative;
   border: 4px double skyblue;
   background: linear-gradient(135deg, #ffe6f7, #f0e6ff, #d6f0ff);
   background-size: 300% 300%;
   animation: gradientFlow 3s ease infinite;
   border-radius: 6px;
}

@keyframes gradientFlow {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

q::before {
   content: "";
   position: absolute;
   top: -12px;
   right: -14px;
   width: 20px;
   height: 20px;
   background: url(https://files.catbox.moe/h76jsl.png) no-repeat center/contain;
   transform: rotate(15deg);  
}

#chat .mes_block .mes_text :not(pre code) > em,
#chat .mes_block .mes_text i:not(pre code i):not(i[class*="fa-"]) {
   display: inline-block;
   position: relative;
   background: transparent;
   padding-left: 20px;
}

#chat .mes_block .mes_text :not(pre code) > em::before,
#chat .mes_block .mes_text i:not(pre code i):not(i[class*="fa-"])::before {
   content: "";
   position: absolute;
   bottom: 2px;
   left: -5px;
   width: 30px;
   height: 30px;
   background: url(https://files.catbox.moe/2uer5i.png) no-repeat center/contain;
}

#chat .mes_block .mes_text :not(pre code) > em::after,
#chat .mes_block .mes_text i:not(pre code i):not(i[class*="fa-"])::after {
   content: "";
   position: absolute;
   left: 0;
   bottom: 0;
   width: 100%;
   height: 2px;
   background: linear-gradient(90deg, #ff80bf, #809fff, #ff80bf);
   background-size: 200% 100%;
   animation: barFlow 2s linear infinite;
}

@keyframes barFlow {
  0% { background-position: 0% 50%; }
  100% { background-position: 100% 50%; }
}

.mes_text pre code {
   background: rgba(255,255,255,0.5);
   border-left: 2px dashed blue;
   border-right: 2px dashed blue;
   border: none;
   background-image: url(https://files.catbox.moe/fldk6m.png);
   background-position: right;
   background-repeat: repeat-y;
   background-size: contain;
   color: #998c9b!important;
}

