input[type='checkbox'] {
  width: 14px;
  height: 14px;
  cursor: pointer;
  outline: none;
  background: rgb(0,0,0,0.2);
  border: 1px solid white;
}

input[type='checkbox']:checked {
  background: black;
}

input[type='checkbox']:checked::after {
  content: "☯︎";
  position: absolute;
  font-size: 15px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
}


#top-bar,
#send_form {
  background: url(https://i.ibb.co/205FLRTs/1.jpg) no-repeat center/cover;
}

#top-bar {
   box-shadow: 5px 5px 10px black;
}

#send_form {
   box-shadow: -5px -5px 10px black;
}


.menu_button {
  border: 1px outset #e8c040;
  border-radius: 3px;
  background: #2b2b2b;
}

.fa-chevron-circle-down:before, .fa-circle-chevron-down:before {
