.popup {
  border-radius: 1px;
  background: url(https://i.ibb.co/YBXtC8nF/IMG-20250413-171542.jpg) no-repeat center/cover;
}


input[type="range"] {
  border-radius: 1px!important;
  background: linear-gradient(to right, gold 0%, black 100%)!important;
  background-blend-mode: overlay;
  box-shadow: inset 0 0 5px #000!important;
