/* ===聊天界面布局代码，引用自类脑社区双色盒子美化作品，作者@kakaa，已获得授权=== */
:root {
  --mes-width: 100%;
  /* 此百分比调整聊天气泡与整体聊天界面的距离 */
  --mes-block-width: 98%;
  /* 此百分比调整聊天记录文字与气泡左右边框的距离 */
  --box-border-radius: 5px;
  /* 所有盒子的圆角大小 */
  --top-distance: 5px;
  /* 顶栏与上边沿的距离 */
  --bottom-distance: 5px;
  /* 底栏与下边沿的距离 */
  --chat-send-spacing: 5px;
  /* 聊天界面与发送框的距离 */
  --leftRight-chat-spacing: 10px;
  /* 聊天界面与左右边沿的距离，电脑端专供 */
  --scroll-width: 5px;
  /* 滚动条宽度 */
  --icon-size: var(--mainFontSize);
  /* 图标大小 */
  --border-radius-small: 5px;
  --border-radius-medium: 10px;
  --spacing-long: 10px;
  --spacing-short: 5px;
}

#chat {
  overflow-y: scroll;
  align-items: center;
  max-height: calc(100dvh - var(--topBarBlockSize) - 20px) !important;
  padding: calc(var(--scroll-width) + 4px) 0 var(--scroll-width)
    var(--scroll-width);
  border-radius: 0 0 var(--box-border-radius) var(--box-border-radius);
  background-color: var(--chat-background-color);
}

@supports (-webkit-touch-callout: none) {
  #chat {
    padding-right: var(--scroll-width);
  }
}

.mesAvatarWrapper {
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.mesAvatarWrapper .avatar {
  margin: calc(var(--mainFontSize)) 0 calc(var(--mainFontSize) * 0.4) 0;
}

.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: contents;
  color: var(--text-color-sec);
}

#chat .ch_name {
  position: relative;
  align-items: flex-end;
  padding: calc(var(--mainFontSize) * 0.8) 0;
}

#chat .ch_name::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 5px;
  background: url(https://i.ibb.co/Xr8kLv9p/06.png) repeat left/cover;
} 

.mes {
  width: var(--mes-width);
  flex-direction: column;
  align-items: center;
  border: none !important;
  background-color: var(--ui-color-main) !important;
  margin: 0;
}

.mes_text {
  padding: calc(var(--mainFontSize) * 0.8) 0 0 0;
}

.mes_block {
  width: var(--mes-block-width);
  padding-left: 0;
}

.last_mes .mes_text {
  padding-right: 0;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0;
}

/* ===聊天界面设计=== */
.mes_button.mes_edit {
  width: 20px;
  height: 20px;
  background: url(https://i.ibb.co/PG5R4Z4j/06.png) no-repeat center/contain;
  opacity: 1;
  filter: none;
}

.mes_button.extraMesButtonsHint {
  width: 20px;
  height: 20px;
  background: url(https://i.ibb.co/Gf7Z7RPS/03.png) no-repeat center/contain;
  opacity: 1;
  filter: none;
}

.mes_button.mes_edit::before,
.mes_button.extraMesButtonsHint::before {
  display: none;
}

.swipe_left {
    bottom: 20px;
    left: 8px;
}

.mes[is_user='false'] .mesAvatarWrapper .avatar::after {
  content: ""; 
  position: absolute;
  bottom: 17px; 
  left: 10%;
  width: 50px;
  height: 50px;
  transform: translate(-50%, -50%); /* 精确让中心点对齐 */
  background: url(https://i.ibb.co/QvZ4Wz6p/03.png) no-repeat center / contain;
  pointer-events: none; 
  z-index: 1;
}

.mes[is_user='false'] .mesAvatarWrapper  {
   position: relative;
   width: 100%;
   background: url(https://i.ibb.co/fdzBMtLy/user.jpg) no-repeat top/cover;
}

.mes[is_user='true'] .mesAvatarWrapper .avatar::after {
  content: ""; 
  position: absolute;
  top: 7%; 
  right: 0;
  width: 50px;
  height: 50px;
  transform: translate(-50%, -50%); /* 精确让中心点对齐 */
  background: url(https://i.ibb.co/39mv2Gr5/0301.png) no-repeat center / contain;
  pointer-events: none; 
  z-index: 5;
}

.mes[is_user='true'] .mesAvatarWrapper {
  width: 100%;
  background: url(https://i.ibb.co/W47kSkmD/char.jpg) no-repeat top/cover;
}


@keyframes pulseGlow {
  0%, 100% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
}


.mes_block {
  width: 100%;
  background: rgba(53,47,54,0.5);
  border: 4px double rgba(232,208,128,0.5);
  border-top: 0px;
  border-radius: 0px 0px 12px 12px;
  line-height: 1.5;
  padding: 8px;
}


.mes_text {
  border: 1px dotted #535f76;
  padding: 0.2em 0.5em;
  background: rgba(67,71,86,0.5);
}

.mes_text pre code {
  border: 2px dashed #FFD700;
  background: rgba(96,93,101,0.8) url(https://files.catbox.moe/rgp8dx.png) no-repeat right/contain;
}

.mes_text pre code::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 50px;
  height: 50px;
  background: url(https://files.catbox.moe/58xf30.png) no-repeat center/contain;
}

/* ===角色卡面板=== */
#rm_print_characters_block {
   border: black;
   border-radius: 8px;
   box-shadow: inset 2px 2px 5px rgba(0, 0, 0, 0.3);
}

.character_select {
  border: 5px solid transparent;
  border-left: none;
  border-right: none;
  border-radius: 8px;
  border-image: url(https://i.ibb.co/Z35r5tB/8b7c22adb17e4139f58de48a4efaf8d8.jpg)  50 fill stretch;
  color: white;
}

#description_textarea {
  background: url(https://files.catbox.moe/a4qhtj.png) no-repeat right/contain;
}


/* ===世界书面板=== */
.wi-card-entry {
  border: none;
  border-radius: 8px;
  background: rgba(59, 42, 32,0.5);
}

/* ===一些其它样式=== */
hr,
#completion_prompt_manager .completion_prompt_manager_list_separator hr {
   width: 100%;
   height: 20px;
   background: url(https://i.ibb.co/Kj7Crr9w/remove-03.png) no-repeat center/cover;
   opacity: 1;
}

::-webkit-scrollbar {
  width: 4px;
  height: 2px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb:vertical,
::-webkit-scrollbar-thumb:horizontal {
  border: none;
  border-radius: 1px;
  box-shadow: none;
  background-color: gold;
}

.mes_reasoning_header {
  border: 1px solid #e8c040;
  border-radius: 1px;
  background: url(https://files.catbox.moe/tqrug0.png) no-repeat right/contain;
}

.mes_reasoning {
  border: 8px solid transparent;
  background: black url(https://files.catbox.moe/25vjzi.jpg) no-repeat right/contain;
}

/* ===聊天文本样式=== */
q {
  display: inline-block;
  background: url(https://i.ibb.co/spM4TgmT/Pngtree-artistic-ink-rendering-golden-spray-1596956.jpg) no-repeat center/cover;
  color: #302821!important;
  border: 4px double black;
} 

q:hover {
  box-shadow: 0 0 10px gold, inset 0 0 6px #000;
  transform: scale(1.02);
}

#chat .mes_block .mes_text :not(pre code) > em,
#chat .mes_block .mes_text i:not(pre code i):not(i[class*="fa-"]) {
  display: inline-block;
  color: gold;
  background: linear-gradient(to right, #000 0%, #111 40%, #222 100%);
  padding: 2px 6px;
  border-radius: 4px;
  border-left: 3px solid gold;
  border-right: 3px solid #2c2c2c;
  box-shadow: inset 0 0 2px #000, 0 0 6px rgba(255, 215, 0, 0.2);
  text-shadow:
    0 0 2px #000,
    1px 1px 1px rgba(0, 0, 0, 0.6),
    0 0 6px rgba(255, 215, 0, 0.3);
  letter-spacing: 0.5px;
  transition: all 0.3s ease;
}
