/* ===复古色系主题美化=== */
/* ==全局字体引入== */
@import url("https://fontsapi-storage.zeoseven.com/154/main/result.css");

body,
input,
select,
h3>span,
textarea,
ul>li,
.text_pole,
.mes_text pre code,
.menu_button>span,
#send_textarea:placeholder-shown {
    font-family: "Judou Sans UI Kore";
    font-weight: normal;
}

/* ==主题色全局变量== */
:root {
  --sepia-primary: #3d3b75;     /* 主色调 */
  --vintage-secondary: #F2D5AE; /* 次色调 */
  --retro-accent: #383870;      /* 点缀色 */
}

/* ===引用部分=== */
:root {
  --mes-width: 100%;
  /* 此百分比调整聊天气泡与整体聊天界面的距离 */
  --mes-block-width: 98%;
  /* 此百分比调整聊天记录文字与气泡左右边框的距离 */
  --box-border-radius: 5px;
  /* 所有盒子的圆角大小 */
  --top-distance: 5px;
  /* 顶栏与上边沿的距离 */
  --bottom-distance: 5px;
  /* 底栏与下边沿的距离 */
  --chat-send-spacing: 5px;
  /* 聊天界面与发送框的距离 */
  --leftRight-chat-spacing: 10px;
  /* 聊天界面与左右边沿的距离，电脑端专供 */
  --scroll-width: 5px;
  /* 滚动条宽度 */
}

/* ==基础样式== */
/* 标题栏样式 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header, 
#user-settings-block h4, 
.standoutHeader {
    background-image: url('https://i.ibb.co/LdzYvQj6/2-03.jpg');
    margin-bottom: 5px;
    border-radius: 10px;
    padding: 2px 5px;
    border: 1px solid var(--SmartThemeBorderColor);
}

/* 复选框样式 */
input[type="checkbox"] {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  border: 2px solid var(--vintage-secondary);
  background-color: var(--sepia-primary);
  cursor: pointer;
  position: relative;
  vertical-align: middle;
}

input[type="checkbox"]:checked {
  background-color: transparent;
  border-color: var(--vintage-secondary);
}

/* 下拉面板与侧边面板样式 */
.drawer-content {
  background: url('https://i.ibb.co/9mDbrhs3/image.jpg') no-repeat; 
  background-size: cover;
}

/* 按钮样式 */
.menu_button {
  background: url('https://i.ibb.co/pj9q10kW/03.jpg');
}

/* 文本输入框样式（排除发送消息文本输入框）*/
input[type="text"]:not(#send_textarea),
.range-block-counter input,
#tagInput,
textarea:not(#send_textarea) {
    background-color: #d35943;
    color: var(--sepia-primary);
    border: 1px solid var(--vintage-secondary);
    border-radius: 4px;
}

/* 弹出框样式 */
.popup {
    background: url('https://i.ibb.co/gZ85Wsw3/450.jpg');
}

/* 下拉框样式 */
.drawer-content select,.inline-drawer-header {
    font-size: calc(var(--mainFontSize)* 0.95);
    background: var(--vintage-secondary);
    border: 8px solid transparent;
    border-image: url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath fill='%23d0b49f' d='M0 0 L100 0 L100 10 Q80 15 70 10 Q60 5 50 10 Q40 15 30 10 Q20 5 10 10 L0 5 Z M0 100 L100 100 L100 90 Q80 85 70 90 Q60 95 50 90 Q40 85 30 90 Q20 95 10 90 L0 95 Z M0 0 L0 100 L10 90 Q15 80 10 70 Q5 60 10 50 Q15 40 10 30 Q5 20 10 10 L5 0 Z M100 0 L100 100 L90 90 Q85 80 90 70 Q95 60 90 50 Q85 40 90 30 Q95 20 90 10 L95 0 Z'/%3E%3C/svg%3E") 30 stretch;
}

/* 调节显示框 */
.neo-range-input,.text_pole {
   background: var(--vintage-secondary);
}


/* ==导航栏== */
/* 导航栏与底部按钮基础样式 */
.drawer-icon::before,
#options_button::before,
#extensionsMenuButton::before,
#send_but::before {
    content: "";
    background-size: 30px 30px;
    background-repeat: no-repeat;
    background-position: center;
    width: 40px;
    height: 40px;
    display: inline-block;     
}

#leftNavDrawerIcon::before {
   background-image: url('https://i.ibb.co/0V6fFkBy/08-03.jpg');
}


#sys-settings-button .drawer-icon::before {
   background-image: url('https://i.ibb.co/spKHs6v1/09-03.jpg');
}


#advanced-formatting-button .drawer-icon::before {
   background-image: url('https://i.ibb.co/5x50Nkg6/07-03.jpg');
}


#WI-SP-button .drawer-icon::before {
   background-image: url('https://i.ibb.co/7xNf0NqW/06-03.jpg');
}

#user-settings-button .drawer-icon::before {
    background-image: url('https://i.ibb.co/XZZXyKTc/01-03.jpg');
}

#logo_block .drawer-icon::before {
    background-image: url('https://i.ibb.co/k2M0Ftnk/05-03.jpg');
}


#extensions-settings-button .drawer-icon::before {
    background-image: url('https://i.ibb.co/wFR4sxqc/04-03.jpg');
}


#persona-management-button .drawer-icon::before {
    background-image: url('https://i.ibb.co/chzsr7np/03-03.jpg');
}


#rightNavDrawerIcon::before {
   background-image: url('https://i.ibb.co/1Jw0gZKq/02-03.jpg');
}


#options_button::before {
   background-image: url('https://i.ibb.co/Cp971YKG/12-03.jpg');
}


#extensionsMenuButton::before {
   background-image: url('https://i.ibb.co/chQ4PwSf/11-03.jpg');
}


#send_but::before {
   background-image: url('https://i.ibb.co/Qv03tkSN/13-03.jpg');
}

#table_drawer_icon::before {
    background-image: url('https://i.ibb.co/0V6fFkBy/08-03.jpg');
}

/* ===聊天界面引用部分=== */
#sheld {
  top: calc(var(--topBarBlockSize) + var(--top-distance));
  height: calc(100dvh - var(--topBarBlockSize) - var(--top-distance) - 1px);
}

#chat {
  overflow-y: scroll;
  align-items: center;
  max-height: calc(100dvh - var(--topBarBlockSize) - 20px) !important;
  padding: calc(var(--scroll-width) + 4px) 0 var(--scroll-width)
    var(--scroll-width);
  border-radius: 0 0 var(--box-border-radius) var(--box-border-radius);
  background-color: var(--chat-background-color);
}

@supports (-webkit-touch-callout: none) {
  #chat {
    padding-right: var(--scroll-width);
  }
}

.mesAvatarWrapper {
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.mesAvatarWrapper .avatar {
  position: relative; 
  margin: calc(var(--mainFontSize)) 0 calc(var(--mainFontSize) * 0.4) 0;
}

.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: contents;
  color: var(--text-color-sec);
}

#chat .ch_name {
  position: relative;
  align-items: flex-end;
  padding: calc(var(--mainFontSize) * 0.8) 0;
}

#chat .ch_name::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 1px;
  background-color: black;
}

.mes {
  width: var(--mes-width);
  flex-direction: column;
  align-items: center;
  border: none !important;
  background-color: var(--ui-color-main) !important;
  margin: 0;
}

.mes_text {
  padding: calc(var(--mainFontSize) * 0.8) 0 0 0;
}

.mes_block {
  width: var(--mes-block-width);
  padding-left: 0;
}

.last_mes .mes_text {
  padding-right: 0;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0;
}

/* ===聊天气泡=== */
.mes[is_user='false'] .mes_block {
  background-color: #f5e6cb; /* 米色牛皮纸底色 */
  color: #3a3238; /* 深紫褐色文字 */
  padding: 12px;
  position: relative;
  /*border: 2px solid #8e7761; *//* 咖啡棕色边框 */
  box-shadow:
    inset 0 0 30px rgba(138, 121, 104, 0.2),
    2px 2px 5px rgba(0, 0, 0, 0.2);
  background-image:
    linear-gradient(rgba(255, 255, 255, 0.1) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255, 255, 255, 0.1) 1px, transparent 1px),
    radial-gradient(circle at 50px 50px, #f5e6cb 0%, #f5e6cb 70%, #e8d4b2 100%);
  background-size: 20px 20px, 20px 20px, 100% 100%;
  border-radius: 1px;
  /*border-top: 2px solid #b85c38; /* 铁锈红色顶部边框 */
  border-left: 2px solid #b85c38;*/ /* 铁锈红色左侧边框 */
  border: 5px solid transparent;
  border-image:
    repeating-linear-gradient(
      45deg,
      #b85c38, /* 铁锈红色 */
      #b85c38 5px,
      #5c4742 5px, /* 褐红色 */
      #5c4742 10px
    );
}


.mes[is_user='false'] .mes_block::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image:
    linear-gradient(45deg, transparent 65%, rgba(99, 75, 56, 0.1) 70%, transparent 75%),
    linear-gradient(135deg, transparent 65%, rgba(99, 75, 56, 0.1) 70%, transparent 75%),
    linear-gradient(225deg, transparent 65%, rgba(99, 75, 56, 0.1) 70%, transparent 75%),
    linear-gradient(315deg, transparent 65%, rgba(99, 75, 56, 0.1) 70%, transparent 75%);
  background-size: 50px 50px;
  pointer-events: none;
  z-index: 1;
}


.mes[is_user='false'] .mes_block::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(ellipse at center, transparent 80%, rgba(184, 92, 56, 0.15) 100%);
  pointer-events: none;
  z-index: 2;
}

/* ===聊天气泡=== */
.mes[is_user='true'] .mes_block {
  /* 基础样式 */
  background-color: #e8d9c0; /* 老旧纸张色 */
  color: #46352a; /* 深棕色文字 */
  padding: 16px;
  position: relative;

  /* 边框设计 */
  border: 1px solid #8a5d3b;
  border-radius: 2px;

  /* 阴影效果 */
  box-shadow:
    inset 0 0 20px rgba(138, 93, 59, 0.2),
    3px 3px 6px rgba(0, 0, 0, 0.15);

  /* 复古海报纹理 */
  background-image:
    url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 0h40v40H0V0zm40 40h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4V4h4v4zm-8 32h-4v-4h4v4zm-8 0h-4v-4h4v4zm-8 0h-4v-4h4v4zm-8 0H4v-4h4v4zm0-8H4v-4h4v4zm0-8H4v-4h4v4zm0-8H4v-4h4v4zm0-8H4V4h4v4zm8 24h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4V4h4v4zm8 24h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4V4h4v4zm8 24h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4v-4h4v4zm0-8h-4V4h4v4z' fill='%238a5d3b' fill-opacity='0.07' fill-rule='evenodd'/%3E%3C/svg%3E"),
    linear-gradient(to bottom, rgba(232, 217, 192, 0.9), rgba(232, 217, 192, 0.95));
}

/* 添加装饰边框 */
.mes[is_user='true'] .mes_block::before {
  content: "";
  position: absolute;
  top: 5px;
  left: 5px;
  right: 5px;
  bottom: 5px;
  border: 1px dashed rgba(138, 93, 59, 0.3);
  pointer-events: none;
}

/* 添加海报装饰元素 */
.mes[is_user='true'] .mes_block::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image:
    radial-gradient(circle at 10% 30%, rgba(165, 42, 42, 0.15) 0%, rgba(165, 42, 42, 0) 50%),
    radial-gradient(circle at 90% 70%, rgba(165, 42, 42, 0.1) 0%, rgba(165, 42, 42, 0) 45%);
  opacity: 0.8;
  pointer-events: none;
}

/* ---引用文本样式--- */
q {
  display: inline-block;
  position: relative;
  background-color: #f2e8d5;
  color: #614e3e;
  line-height: 1.6;
  margin: 0px 2px;
  padding: 0px 8px;
  border-left: 4px solid #d17c78;
  box-shadow: 3px 3px 0 #bf9d7e;

  /* 复古纸张纹理 */
  background-image: url("data:image/svg+xml,%3Csvg width='50' height='50' viewBox='0 0 50 50' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5z' fill='%23614e3e' fill-opacity='0.08' fill-rule='evenodd'/%3E%3C/svg%3E");
}

/* ---斜体文本样式--- */
#chat .mes_block .mes_text :not(pre code) > em,
#chat .mes_block .mes_text i:not(pre code i):not(i[class*="fa-"]) {
  display: inline-block;
  position: relative;
  font-style: italic;
  color: #5a3e2b;
  padding: 0 8px;
  margin: 0 2px;
  background-color: #f7efdf;
  border-bottom: 2px dotted #86a397;

  /* 复古杂志风格的装饰 */
  background-image: url("data:image/svg+xml,%3Csvg width='52' height='26' viewBox='0 0 52 26' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%2386a397' fill-opacity='0.1'%3E%3Cpath d='M10 10c0-2.21-1.79-4-4-4-3.314 0-6-2.686-6-6h2c0 2.21 1.79 4 4 4 3.314 0 6 2.686 6 6 0 2.21 1.79 4 4 4 3.314 0 6 2.686 6 6 0 2.21 1.79 4 4 4v2c-3.314 0-6-2.686-6-6 0-2.21-1.79-4-4-4-3.314 0-6-2.686-6-6zm25.464-1.95l8.486 8.486-1.414 1.414-8.486-8.486 1.414-1.414z' /%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
  text-shadow: 1px 1px 0 rgba(255, 255, 255, 0.8);
}

/* ---反引号文本样式--- */
/* 代码块主样式 - 复古终端/打字机风格 */
.mes_text pre code {
  display: inline-block !important;
  font-family: 'Courier New', monospace !important;
  background-color: gray !important;
  color: #e3d9c4 !important;
  padding: 2px 5px !important;
  border-radius: 3px !important;
  font-size: 0.9em !important;
  letter-spacing: 0.5px !important;
  box-shadow: inset 0 0 2px rgba(0, 0, 0, 0.7), 0 2px 0 rgba(255, 255, 255, 0.07) !important;
  margin: 0 3px !important;
  border: 1px solid #444 !important;
  text-shadow: 0 1px 0 rgba(0, 0, 0, 0.4) !important;
}

/* 多行代码块样式 */
.mes_text pre code {
  display: block !important;
  background-color: #2b2b2b !important;
  color: #e3d9c4 !important;
  padding: 15px !important;
  margin: 15px 0 !important;
  overflow-x: auto !important;
  line-height: 1.5 !important;
  border-radius: 5px !important;
  border: 1px solid #444 !important;
  box-shadow:
    inset 0 0 10px rgba(0, 0, 0, 0.5),
    0 2px 4px rgba(0, 0, 0, 0.2) !important;

  /* 复古CRT显示器效果 */
  background-image:
    linear-gradient(0deg, rgba(0, 0, 0, 0.1) 1px, transparent 1px) !important;
  background-size: 100% 2px !important;
  position: relative !important;
}

/* 代码块顶部装饰 - 模拟老式终端标题栏 */
.mes_text pre code::before {
  content: "Code" !important;
  display: block !important;
  position: absolute !important;
  top: 0px !important;
  left: 10px !important;
  background-color: #e76b5d !important;
  color: #2b2b2b !important;
  padding: 0 10px !important;
  font-size: 0.8em !important;
  font-weight: bold !important;
  border-radius: 3px !important;
  border: 1px solid #444 !important;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2) !important;
}

/* 打字机效果的光标 */
.mes_text pre code::after {
  content: "_" !important;
  display: inline-block !important;
  color: #e76b5d !important;
  animation: blink 1s step-end infinite !important;
}

/* 光标闪烁动画 */
@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
}



/* ===头像样式=== */
.mes:not([is_user='true']) .mesAvatarWrapper .avatar::after {
  content: ""; 
  position: absolute;
  top: 50%; 
  left: 50%;
  width: 120%;
  height: 120%;
  transform: translate(-50%, -50%); /* 精确让中心点对齐 */
  background: url('https://i.ibb.co/ynNZxWDB/retouch-2025032100560626.png') no-repeat center / contain;
  pointer-events: none; 
}


.mes[is_user='true'] .mesAvatarWrapper .avatar::after {
  content: ""; 
  position: absolute;
  top: 50%; 
  left: 50%;
  width: 120%;
  height: 110%;/* 调整高度 */
  transform: translate(-50%, -50%); /* 精确让中心点对齐 */
