/* ===全局字体样式=== */
@import url('https://static.zeoseven.com/zsft/181/main/result.css');

body,
input,
select,
h3>span,
textarea,
ul>li,
.text_pole,
.mes_text pre code,
.menu_button>span,
#send_textarea:placeholder-shown  {
   font-family: "未来圆SC";
   font-weight: normal;
