  border-radius: 5px;
  box-shadow: 0 3px 10px var(--moonlight-shadow);
  position: relative;
  background: url(https://i.ibb.co/r2KbMFzS/3322811e89c3f1fba4aea6b7061d5459.jpg) repeat center;
  background-size: contain;
}

.mes[is_user='true'] .mesAvatarWrapper::before {
  content: "";
